import React, { useEffect, useRef, useState } from "react";

function PixelGenerator() {
    const [heigthValue, setHeigthValue] = useState(1)
    const [widthValue, setWidthValue] = useState(1)
    const [colorValue, setColorValue] = useState( '#fff' )

    const wrapper = useRef()

    let events = {
        mouse: {
            down: "mousedown",
            move: "mousemove",
            up: "mouseup"
        },
        touch: {
            down: "touchstart",
            mobe: "touchmove",
            up: "touchend",
        },
    };
    
    let deviceType = "";

    let draw = false;
    let erase = false;

    const isTouchDevice = () => {
        try {
            document.createEvent('TouchEvent')
            deviceType = 'touch';
            return true;
        } catch (e) {
            deviceType = 'mouse';
            return false;
        }
    }

    useEffect (() => {
        isTouchDevice();
    })

    const createGrid = () => {
        wrapper.current.innerHTML = ''
        let count = 0
        for (let i = 0; i < heigthValue; i++) {
            count += 2;
            let div = document.createElement('div');
            div.classList.add('gridRow')
        
        
            for (let i = 0; i < widthValue; i++) {
                count += 2;
                let col = document.createElement('div');
                col.classList.add('gridCol');
                col.setAttribute('id', `gridCol${count}`);
                col.addEventListener(events[deviceType].down, () => {
                    draw = true;
                    if (erase) {
                        col.style.backgroundColor = 'transparent';
                    } else {
                        col.style.backgroundColor = colorValue;
                    };
                });
                
                col.addEventListener(events[deviceType].move, (e) => {
                    let elementId = document.elementFromPoint(
                        !isTouchDevice() ? e.clientX : e.touches[0].clientX,
                        !isTouchDevice() ? e.clientY : e.touches[0].clientY,
                    ).id;
                    checker(elementId);
                });

                col.addEventListener(events[deviceType].up, () => {
                    draw = false;
                });

                div.apppendChild(col);
        }
        wrapper.apppendChild(div)
        
    }
};

function checker(elementId) {
    
}



    return (
    <div className="container flex bg-slate-50 h-1/3 w-1/2 p-10 flex-col">
      <div className="controls flex justify-between items-center">
        <div className="width  flex flex-col">
          <label className="" for="width">Width</label>
          <input type="range" min="1" max="35" name="width" 
          value={widthValue}
          onChange={(e) => setWidthValue(e.target.value)}></input>
          <span>{widthValue     }</span>
        </div>

        <div className="heigth flex flex-col">
          <label className="" for="heigth">Heigth</label>
          <input type="range" min={1} max={35} name="heigth"
          value={heigthValue}
          onChange={(e) => setHeigthValue(e.target.value)}></input>
          <span>{heigthValue}</span>
        </div>

      
      </div>

      <div className="flex justify-between items-center mt-10">
            <button className="bg-green-500 p-2 rounded-md"
            onClick={createGrid}>Create grid</button>
            <button className="bg-green-500 p-2 rounded-md">Clear grid</button>
            <input type="color"
            value={colorValue}
            onChange={(e) => setColorValue(e.target.value)}></input>
            <button className="bg-green-500 p-2 rounded-md">Erase</button>
            <button className="bg-green-500 p-2 rounded-md">Paint</button>
        </div>

        <div className="container mt-5" ref={wrapper}></div>
    </div>
  );
}

export default PixelGenerator;
