import React, { useState, useRef, useEffect } from 'react';

const PixelGenerator = () => {
  const [rows, setRows] = useState(16);
  const [cols, setCols] = useState(16);
  const [drawing, setDrawing] = useState(false);
  
  const wrapperRef = useRef(null);

  useEffect(() => {
    createGrid();
  }, [rows, cols]);

  const createGrid = () => {
    const grid = [];
    for (let i = 0; i < rows; i++) {
      const row = [];
      for (let j = 0; j < cols; j++) {
        row.push(<div key={`${i}-${j}`} className="cell" onMouseOver={handleCellHover} onMouseDown={handleCellMouseDown} />);
      }
      grid.push(<div key={i} className="row">{row}</div>);
    }
    setRows(grid);
  };

  const handleSizeChange = (e) => {
    if (e.target.name === 'rows') {
      setRows(e.target.value);
    } else if (e.target.name === 'cols') {
      setCols(e.target.value);
    }
  };

  const handleCellMouseDown = () => {
    setDrawing(true);
  };

  const handleCellHover = (e) => {
    if (drawing) {
      e.target.style.backgroundColor = '#000000'; // Черный цвет для рисования
    }
  };

  const handleMouseUp = () => {
    setDrawing(false);
  };

  return (
    <div>
      <div>
        <label htmlFor="rows">Rows: </label>
        <input type="range" min="1" max="30" name="rows" value={rows} onChange={handleSizeChange} />
        <span>{rows}</span>
      </div>
      <div>
        <label htmlFor="cols">Columns: </label>
        <input type="range" min="1" max="30" name="cols" value={cols} onChange={handleSizeChange} />
        <span>{cols}</span>
      </div>
      <div ref={wrapperRef} onMouseUp={handleMouseUp}>{grid}</div>
    </div>
  );
};

export default PixelArtDrawer;
