import React, { useState } from 'react';
import useSound from 'use-sound';
import firstSong from './assets/music/1.mp3';
import secondSong from './assets/music/2.mp3';

const Player = () => {
  const [currentSong, setCurrentSong] = useState(firstSong);
  const [play] = useSound(currentSong);

  const handlePlayNext = () => {
    setCurrentSong(secondSong);
  };

  const handlePlayPrevious = () => {
    setCurrentSong(firstSong);
  };

  return (
    <div>
      <button onClick={play}>Play Current Song</button>
      <button onClick={handlePlayNext}>Play Next Song</button>
      <button onClick={handlePlayPrevious}>Play Previous Song</button>
    </div>
  );
};

export default MyComponent;
