import React, { useState } from 'react';

const PixelArtApp = () => {
  const [color, setColor] = useState("#000000"); // Начальный цвет - черный
  const [drawing, setDrawing] = useState(false);
  
  const handleColorChange = (e) => {
    setColor(e.target.value);
  };

  const handleMouseDown = () => {
    setDrawing(true);
  };

  const handleMouseOver = (e) => {
    if (drawing) {
      e.target.style.backgroundColor = color;
    }
  };

  const handleMouseUp = () => {
    setDrawing(false);
  };

  return (
    <div>
      <input type="color" value={color} onChange={handleColorChange} />
      <div style={{ display: "grid", gridTemplateColumns: "repeat(16, 20px)", gap: "1px" }}>
        {[...Array(256)].map((_, index) => (
          <div
            key={index}
            style={{ width: "20px", height: "20px", border: "1px solid #ccc" }}
            onMouseDown={handleMouseDown}
            onMouseOver={handleMouseOver}
            onMouseUp={handleMouseUp}
          />
        ))}
      </div>
    </div>
  );
};

export default PixelArtApp;
