import React, { useEffect, useRef, useState } from "react";

function PixelGenerator() {
    const [heigthValue, setHeigthValue] = useState(1)
    const [widthValue, setWidthValue] = useState(1)
    const [colorValue, setColorValue] = useState( 'red' )
    const [rows, setRows] = useState([])
    
    const [erase, setErase] = useState(false)
    const [draw, setDraw] = useState(true)


    const wrapper = useRef()

    const createGrid = () => {
        const grid = [];
        for (let i = 0; i < heigthValue; i++) {
          const row = [];
          for (let j = 0; j < widthValue; j++) {
            row.push(<div key={`${i}-${j}`} id={`${j}`}
            className="gridcol"
            onMouseDown={onDownCol}
            onTouchStart={onDownCol}

             onMouseUp={onUpCol}
            onTouchEnd={onUpCol}

            onTouchMove={onMoveCol}
            onMouseOver={onMoveCol}

            />);
          }
          grid.push(<div key={i} className="flex">{row}</div>);
        }
        setRows(grid);
      };

    function onDownCol (e) {
        console.log('down')
        setDraw(true)
        if (erase) {
            e.target.style.backgroundColor = 'transparent';
        } else {
            console.log(colorValue)
            e.target.style.backgroundColor = colorValue;
        };
    }

    function onMoveCol (e) {
        console.log('down')
        if (erase) {
            e.target.style.backgroundColor = 'transparent';
        } else if (draw) {
            console.log(colorValue)
            e.target.style.backgroundColor = colorValue;
        };
    }

    function onUpCol () {
        setDraw(false)
    }

    function clearRows () {
        setRows([])
    }


    return (
    <div className="container flex bg-slate-50 h-auto w-1/2 p-10 flex-col">
      <div className="controls flex justify-between items-center">
        <div className="width  flex flex-col">
          <label className="" htmlFor="width">Width</label>
          <input type="range" min={1} max={35} name="width" 
          value={widthValue}
          onChange={(e) => setWidthValue(e.target.value)}></input>
          <span>{widthValue     }</span>
        </div>

        <div className="heigth flex flex-col">
          <label className="" htmlFor="heigth">Heigth</label>
          <input type="range" min={1} max={35} name="heigth"
          value={heigthValue}
          onChange={(e) => setHeigthValue(e.target.value)}></input>
          <span>{heigthValue}</span>
        </div>

      
      </div>

      <div className="flex justify-between items-center mt-10">
            <button className="bg-green-500 p-2 rounded-md"
            onClick={createGrid}>Create grid</button>
            <button className="bg-green-500 p-2 rounded-md" onClick={clearRows}>Clear grid</button>
            <input type="color"
            value={colorValue}
            onChange={(e) => setColorValue(e.target.value)} />
            <button className="bg-green-500 p-2 rounded-md">Erase</button>
            <button className="bg-green-500 p-2 rounded-md">Paint</button>
        </div>

        <div className="container mt-5" ref={wrapper}>
           {rows}
        </div>
    </div>
  );
}

export default PixelGenerator;
