import React, { useState } from 'react';

const TextEditor = () => {
  const [content, setContent] = useState(''); // Состояние для хранения содержимого редактора

  // Обработчик события изменения содержимого редактора
  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  // Функция для применения стиля "жирный" к выделенному тексту
  const applyBoldStyle = () => {
    document.execCommand('bold', false, null);
  };

  return (
    <div className='text-editor'>
      <div>
        <button onClick={applyBoldStyle}>Bold</button>
      </div>
      <div
        contentEditable
        className='editor-content'
        onInput={handleContentChange}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </div>
  );
};

export default TextEditor;
